WebLink.url:
// Enable Ajax Toolkit
{!REQUIRESCRIPT("/soap/ajax/8.0/connection.js")} 

// Get the Ids of the selected records
var records = {!GETRECORDIDS( $ObjectType.Courier1__Courier_Schedule__c )};
var schedRecords = [];

// Update the selected records
if( records[0] == null )
{
    alert("Please select at least one row");
}
else
{
    for ( var n in records ) {
        var c = new sforce.SObject("Courier1__Courier_Schedule__c");
        c.id = records[n];
        c.Courier1__Enabled__c = true;
        schedRecords.push(c);
    }
}

// Perform the update
result = sforce.connection.update(schedRecords);

// Reload the window
window.location.reload();
